class Smartcat_Vivita_AddOns {

    public function __construct() {
        
        $this->add_hooks();
        if ( class_exists( 'SmartcatModulesPlugin' ) ) :
            $this->init_modules();
        endif;
    }

    public function add_hooks() {

        // Add Hooks for Custom CSS and JS
        add_action( 'wp_head', array ( $this, 'vivita_custom_js' ) );
        add_action( 'wp_head', array ( $this, 'vivita_custom_css' ) );
        
        // Add Filters
        add_filter( 'the_content',          array( $this, 'vivita_add_social' ) );
        add_filter( 'widget_text', 'do_shortcode' );
        
        add_action( 'admin_menu',           array( $this, 'vivita_add_menu_pages' ) );
//        add_action( 'after_setup_theme',    array( $this, 'vivita_theme_updater' ) );
        
//        if( ! vivita_strap_pl() ) :
//            add_action( 'admin_notices', array( $this, 'vivita_admin_notice__success' ) );
//        endif;        
        
    }

    public function init_modules() {

        SmartcatModulesPlugin::instance( array (

            get_theme_mod( 'smartcat_toggle_call_to_action',    'include' ) == 'exclude' ? null : 'call_to_action',
            get_theme_mod( 'smartcat_toggle_clients',           'include' ) == 'exclude' ? null : 'client',
            get_theme_mod( 'smartcat_toggle_contact_form',      'include' ) == 'exclude' ? null : 'contact_form',
            get_theme_mod( 'smartcat_toggle_contact_info',      'include' ) == 'exclude' ? null : 'contact_info',
            get_theme_mod( 'smartcat_toggle_events',            'include' ) == 'exclude' ? null : 'event',
            get_theme_mod( 'smartcat_toggle_faqs',              'include' ) == 'exclude' ? null : 'faq',
            get_theme_mod( 'smartcat_toggle_gallery',           'include' ) == 'exclude' ? null : 'gallery',
            get_theme_mod( 'smartcat_toggle_news',              'include' ) == 'exclude' ? null : 'news',
            get_theme_mod( 'smartcat_toggle_pricing_table',     'include' ) == 'exclude' ? null : 'pricing_table',
            get_theme_mod( 'smartcat_toggle_projects',          'include' ) == 'exclude' ? null : 'project',
            get_theme_mod( 'smartcat_toggle_testimonials',      'include' ) == 'exclude' ? null : 'testimonial',
            get_theme_mod( 'smartcat_toggle_work_history',      'include' ) == 'exclude' ? null : 'position',
            
        ) );
        
    }
    
    function vivita_add_menu_pages(){
        add_menu_page('Vivita', 'Vivita Pro', 'manage_options', 'vivita_menu', array( $this, 'vivita_menu' ), get_template_directory_uri() . '/inc/images/cat_logo_mini.png' );
    }
    
    function vivita_menu() {
        include get_template_directory() . '/inc/vivita/upgrade.php';
    }
        
    function vivita_add_social( $content ) { 

        if( is_single() && get_post_type() == 'post' ) :

            if( get_theme_mod( 'vivita_social_share_toggle', 'show' ) == 'hide' ) : 
                return $content;
            endif;

            $home_url = home_url('/'); 
            
            $content .= '<div class="share-buttons">'; 
 
                $content .= '<h5 id="social-share-heading">' . __( 'Share:', 'vivita' ) . '</h5>'; 

                $content .= '<ul>'; 

                    $content .= '<li><a target="_BLANK" href="https://www.facebook.com/sharer/sharer.php?u=' . get_the_permalink() . '&t=' . get_the_title() . '">' . '<i class="fa fa-facebook"></i>' . '</a></li>';
                    $content .= '<li><a target="_BLANK" href="https://twitter.com/intent/tweet?source=' . get_the_permalink() . '&text=:%20' . get_the_permalink() . '">' . '<i class="fa fa-twitter"></i>' . '</a></li>';
                    $content .= '<li><a target="_BLANK" href="https://plus.google.com/share?url=' . get_the_permalink() . '">' . '<i class="fa fa-google-plus"></i>' . '</a></li>';
                    $content .= '<li><a target="_BLANK" href="http://www.linkedin.com/shareArticle?mini=true&url=' . get_the_permalink() . '&title=' . get_the_title() . '">' . '<i class="fa fa-linkedin"></i>' . '</a></li>';
                    $content .= '<li><a target="_BLANK" href="http://pinterest.com/pin/create/button/?url=' . get_the_permalink() . '&description=' . get_the_title() . '">' . '<i class="fa fa-pinterest"></i>' . '</a></li>';

                $content .= '</ul>'; 
                
            $content .= '</div>'; 
            
        endif;

        return $content;
        
    }
    
    /**
     * TODO: COPY THIS FOR SHORTCODE CONTENT
     * @return type
     */
    function vivita_events() {

        ob_start();
        $this->vivita_output_events();
        $output = ob_get_clean();
        return $output;
    }

    function vivita_admin_notice__success() {
        ?>
        <div class="notice notice-error is-dismissible">
            <h3> <?php esc_html_e( 'Your', 'vivita' ); ?> Vivita <?php esc_html_e( 'license is not active.', 'vivita' ); ?></h3>
            <p>
                <a href="<?php echo esc_url( admin_url('themes.php?page=vivita-pro-license') ); ?>" class="button button-primary"><?php esc_html_e( 'Begin activating', 'vivita' ); ?> Vivita Pro</a>
            </p>
        </div>
        <?php
    }
    
    function vivita_custom_css() {
        
        // Custom CSS
        if ( get_theme_mod( 'vivita_custom_css', '' ) != '' ) :

            echo '<style type="text/css">' . get_theme_mod( 'vivita_custom_css', '' ) . '</style>';

        endif;
        
    }

    function vivita_custom_js() {
        
        // Custom JS / Google Analytics Script
        if( get_theme_mod( 'vivita_custom_js', '' ) != '' ) :

            echo '<script type="text/javascript">' . get_theme_mod( 'vivita_custom_js', '' ) . '</script>';

        endif;
        
    }

}

function vivita_send_message(){

    $name = sanitize_text_field( $_POST['name'] );
    $email = sanitize_text_field( $_POST['email'] );
    $message_entered = sanitize_text_field( $_POST['message'] );
    $recipient_email = sanitize_text_field( $_POST['recipient'] );

    $message = 'From: ' . $name . ' || Sender Email: ' . $email . ' || Message: ' . $message_entered;

    //    $widget = new Vivita_Contact_Form_Widget();
    //    $settings = $widget->get_settings();
    //    $settings = reset( $settings );
    
    wp_mail( $recipient_email, __( 'New message from ' . get_option('blog_name'), 'vivita' ), $message );
    
    echo 1;
    exit();

}
add_action('wp_ajax_vivita_send_message', 'vivita_send_message' );
add_action('wp_ajax_nopriv_vivita_send_message', 'vivita_send_message' );


